/* CUSTOM THEME STYLE INPUTS 自訂主題樣式輸入
- - - - - - - - - - - - - - - - - */

:root {
  --custom-theme-style-inputs: [
  {
    "type": "color",
    "varId": "customThemeColor",
    "displayText": "Theme Primary Color",
    "default": "rgba(81, 160, 222, 1)"
  },
  {
    "type": "color",
    "varId": "customThemeColor2",
    "displayText": "Theme Secondary Color",
    "default": "rgba(250, 198, 121, 1)"
  },
  {
    "type": "color",
    "varId": "customTopBarColor",
    "displayText": "Top Bar Color",
    "default": "rgba(23, 23, 23, 0.7)"
  },
  {
    "type": "color",
    "varId": "Drawer-iconColor",
    "displayText": "Drawer icon Color",
    "default": "rgba(255, 255, 255, 0.8)"
  },
  {
    "type": "slider",
    "varId": "customCSS-bg-blur",
    "displayText": "Background Image Blur Strength",
    "default": "3",
    "min": 0,
    "max": 10,
    "step": 1
  },
  {
    "type": "color",
    "varId": "sheldBackgroundColor",
    "displayText": "#sheld Background Color",
    "default": "rgba(0, 0, 0, 0.2)"
  },
  {
    "type": "slider",
    "varId": "sheldBlurStrength",
    "displayText": "#sheld Blur Strength",
    "default": "5",
    "min": 0,
    "max": 10,
    "step": 1
  },
  {
    "type": "select",
    "varId": "customCSS-top-padding",
    "displayText": "#chat Top Padding",
    "default": "25px",
    "options": [
      {
        "label": "ON",
        "value": "25px"
      },
      {
        "label": "OFF",
        "value": "0px"
      }
    ]
  },
  {
    "type": "select",
    "varId": "customCSS-chat-gradient-blur",
    "displayText": "#chat Gradient Blur",
    "default": "Default",
    "options": [
      {
        "label": "Default",
        "value": "linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 1) 8%, rgba(0, 0, 0, 1) 92%, rgba(0, 0, 0, 0) 100%)"
      },
      {
        "label": "Small",
        "value": "linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 1) 4%, rgba(0, 0, 0, 1) 96%, rgba(0, 0, 0, 0) 100%)"
      },
      {
        "label": "None",
        "value": "none"
      }
    ]
  },
  {
    "type": "text",
    "varId": "custom-ChatAvatar",
    "displayText": "#chat Avatar Width & Height",
    "default": "40px"
  },
  {
    "type": "select",
    "varId": "VN-sheld-height",
    "displayText": "Visual Novel Mode #sheld Height",
    "default": "40dvh",
    "options": [
      {
        "label": "40dvh",
        "value": "40dvh"
      },
      {
        "label": "50dvh",
        "value": "50dvh"
      },
      {
        "label": "60dvh",
        "value": "60dvh"
      }
    ]
  }
]
}
